  //////// PLACEHOLDER TEXT DISAPPEARS ON FOCUS ///////

  var placeText;
 
  $('.focus-hide').focus(function() {
    placeText = $(this).attr('placeholder');
    $(this).css('color', 'transparent');
    $(this).attr('placeholder','').delay('500').css('color', 'black');
  }).blur(function() {
    $(this).attr('placeholder', placeText);
  }).blur();













